import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Submit from '../componentes/Submit'
import Home from '../componentes/Home'

const DetailPage = () => {
  const [product, setProduct] = useState([]);
  useEffect(() => {
    GetDatos();
  }, []);
  const { id } = useParams();
  const productos = id;

  const GetDatos = async () => {
    const URL = "http://localhost:3000/getProduct/";
    const response = await fetch(URL + `${productos}`);
    const { item } = await response.json();

    console.log(item);
    const Data = item.map((data) => {
      return {
        id: data.id,
        condition: data.condition,
        title: data.title,
        img: data.picture,
        description: data.description,
        condition: data.condition,
        soldQuantity: data.sold_quantity,
        price: data.price.amount,
      };
    });
    //  return Data
    setProduct(Data);
  };

  return (
    <div>
     <Home/>
      {product.map((item) => (
        <div className="detail-product">
        
        <div className="flex row">
        <div className="detail-product__img-container">
            <img src={item.img} />
            </div>
            <div >
            <p>{item.condition}</p>
          <p>{item.id}</p>
          <h1>{item.title}</h1>
            <p>{item.condition}</p>
            <p>{item.soldQuantity}</p>
            <p>${item.price}</p>
            <button className="detail-product__btn">Comprar</button>
            </div>
        </div>
          
          <h2>Descripci√≥n del producto</h2>
      <p>{item.description}</p>
        </div>
      ))}
    </div>
  );
};

export default DetailPage;
