            e.preventDefault()
            debugger
            if(inputText.trim().length > 3){
                debugger
                setProduct(Product =>
                    [inputText]);
                    setInputText('')
              history.push(`/items/search/${inputText}`)
              
            }
        

    }
 

 

return(
   

    <div className="search-container">
        <div className="search-content">
        <img className="logo" alt="Logo" src={logoML}/>
        <form onSubmit={formSubmit}>
    <div className="input-container">
    <input className="input-field" value={inputText} placeholder="Nunca dejes de buscar" type="text" onChange={inputValue}/>
    
    <img src={search} alt="Buscador"/>
 
    </div>
        
    </form>
    </div>
    </div>

)

}



export default withRouter(Submit)
